//Approach-3 (Converting the question to Longest Common Subsequence) O(n*n)
/*
	If we find LCS between nums and it's sorted form, we will find LIS :-)
*/
class Solution {
public:
    int LCS(vector<int>& nums1, vector<int>& nums2) {
        int m = nums1.size();
        int n = nums2.size();

        vector<vector<int>> t(m+1, vector<int>(n+1));

        for(int i = 0; i<m+1; i++) {
            for(int j = 0; j<n+1; j++) {
				if(i == 0 || j == 0) continue;

                if(nums1[i-1] == nums2[j-1])
                    t[i][j] = 1 + t[i-1][j-1];
                else
                    t[i][j] = max(t[i-1][j], t[i][j-1]);
            }
        }

        return t[m][n];
    }
    int lengthOfLIS(vector<int>& nums) {
        //We need unique elements only
		//because we want'Longest "Increasing" Subseqnece'
        set<int> st(begin(nums), end(nums));

        vector<int> nums2(begin(st), end(st));

        return LCS(nums, nums2);

    }
};
