class Solution {
    public int lengthOfLIS(int[] input) {
        int maxLength = 0;
        for(int i = 0; i < input.length  && input.length - i > maxLength; i++) {
            maxLength = Math.max(maxLength, maxIncreasingSubsequenceLength(input, input[i], i+1, 1, 0));
        }  
        return maxLength;
    }

    int maxIncreasingSubsequenceLength(int[] input, int prevValue, int nextIndex, int lengthSoFar, int maxLength) {
        if(nextIndex >= input.length)  {
            return lengthSoFar;
        }
        for(int i = nextIndex; i < input.length; i++) {
            if (prevValue < input[i]) {
                // Get max length by considering current element
                int lengthWithCurrent = maxIncreasingSubsequenceLength(input, input[i], i+1, lengthSoFar + 1, maxLength);

                // if length by considering current element is greater than or equal to :
                // remaining elements to be scanned - 1
                // Treat lengthWithCurrent element as maxLength
                if (lengthWithCurrent >= input.length - nextIndex) {
                    return lengthWithCurrent;
                }

                // Get max length without considering current element
                int lengthWithoutCurrent = maxIncreasingSubsequenceLength(input, prevValue, i+1, lengthSoFar, maxLength);

                // get max of above and maxLength
				maxLength = Math.max(maxLength, Math.max(withCurrent, withoutCurrent));
            }
        }
        return Math.max(lengthSoFar, maxLength);
    }
}
