class Solution {
    public int lengthOfLIS(int[] arr) {
        int[] memo = new int[arr.length + 1];
        Arrays.fill(memo, -1 );
        int res = 0;
        for (int i = 0; i < arr.length; i++) {
            res = Math.max(res, dp(arr, i, memo));
        }

        return res;
    }

    private static int dp(int[] arr, int i, int[]memo) {

        if (memo[i] != -1) {
            return memo[i];
        }
        int res = 1;
        for (int j = 0; j < i; j++) {
            if (arr[i] > arr[j]) {
                res = Math.max(res, 1 + dp(arr, j, memo));
            }
        }
        memo[i] = res;
        return memo[i];
    }
}
