class Solution {
public:
    int lengthOfLIS(vector<int>& nums) {
        int n=nums.size();
       vector<int>dp;
       for(int i=0;i<nums.size();i++){
         int pos=lower_bound(dp.begin(),dp.end(),nums[i])-dp.begin();
         if(pos==dp.size()){
             dp.push_back(nums[i]);
         }
         else{
             dp[pos]=nums[i];
         }
       }
       int ans=dp.size();
       return ans; 
    }
};
