        vector<int> next(n+1,0), curr(n+1,0);

        for(int i=n-1;i>=0;i--){
            for(int j=i-1;j>=-1;j--){
                int len= 0+ next[j+1]; //not take  
                if(j==-1 or nums[i]>nums[j])
                    len= max(len,1+ next[i+1]); //take

