class Solution {
public:
    int lengthOfLIS(vector<int>& nums) {
        //Algorithmic approach
        int n=nums.size();
        vector<int> dp(n,1);
        int mx=1;

        for(int i=0;i<n;i++){
            for(int j=0;j<i;j++){  //  check all previous values before i
                if(nums[j]<nums[i])
                    dp[i]=max(dp[i], 1+dp[j]);
            }
            mx=max(mx,dp[i]); // max val in dp array will be the ans
        }

        return mx;
    }
};
