        //binary search
        vector<int> temp;
        int n= nums.size();
        temp.push_back(nums[0]);

        int len = 1;

        //NOTE: temp is not the LIS

        for(int i=1; i<n; i++){
            if(nums[i]>temp.back()){
               // nums[i] > the last element of temp array 

               temp.push_back(nums[i]);
               len++; //stores the size of the LIS

            } 
            else{
            // replacing the values 
                int ind = lower_bound(temp.begin(),temp.end(),nums[i]) - temp.begin();
                temp[ind] = nums[i];
            }

        }

        return len;
    }
};
