    public int incSubSeq(int i, int prev, int[] nums) {
        if(i >= nums.length) return 0;
        if(dp[prev + 1] != -1) return dp[prev + 1];

        int dt = 0, t = incSubSeq(i + 1, prev, nums);                                  // don't take case

        if(prev == -1 || nums[i] > nums[prev])                                   // take cases for subsequence
            dt = Math.max(t, 1 + incSubSeq(i + 1, i, nums));

        return dp[prev + 1] = Math.max(t, dt);
    }
