class Solution {

# SOLUTION 1 using the normal dp logic
    public int lengthOfLIS(int[] nums) {
        // The logic is try to find the max common subsequence till an index i in the nums and store it in the result array
        int n = nums.length;
        int[] result = new int[n];
        result[0] =1;
//         the if you are on i traverse till the beginning and check if current number is greater the the number and if yes then add the subsequence count 
        for(int i=1;i<n;i++)
        {
            for(int j=i-1;j>=0;j--)
                if(nums[i]>nums[j])
                    result[i] = Math.max(result[j]+1,result[i]);
                else
                    result[i]=Math.max(result[i],1);
        }
        int max=1;
        int i=0;
//         the result array has the max count for an index i hence for the whole array return the max count in the end.
        for(int k:result)
            max=Math.max(result[i++],max);

        return max;
    }


# SOLUTION 2
    public int lengthOfLIS(int[] nums) {
<!-- Using set to elimate duplicates -->
        Set<Integer> set = new HashSet<>();
        for(int ele:nums)
        set.add(ele);


        int[] nums1 =new int[set.size()];
        int i=0;
        for(int ele:set)
            nums1[i++]=ele;

<!-- sorting array to get the increasing subsequence -->
        Arrays.sort(nums1);

<!-- finding the lcs to get the desired result -->
        return LCS(nums,nums1);
    }

    int LCS(int[] nums, int[] nums1)
    {
        int m= nums.length;
        int n = nums1.length;
        int[][] dp= new int[m+1][n+1];

        for(int i=1;i<=m;i++)
        {
            for(int j=1;j<=n;j++)
            {
                if(nums[i-1]==nums1[j-1])
                {
                    dp[i][j]=1+dp[i-1][j-1];
                }
                else

                dp[i][j]= Math.max(dp[i-1][j],dp[i][j-1]);
            }
        }
        return dp[m][n];
    }

}






