class Solution {


    public int lengthOfLIS(int[] nums) {

int n = nums.length;

int[] dp= new int[n];

int len = 1;

dp[0] = nums[0];

for(int i=1; i<n; i++){

    if(nums[i]<dp[0]){
       dp[0] = nums[i];
    }else if(nums[i]>dp[len-1]){
       dp[len]  =  nums[i];
       len++; 
    }else{
       dp[binarySearch(dp,-1,len-1,nums[i])] = nums[i];
    }
}

return len;

    }

    public int binarySearch(int[] dp,int left,int right,int target){

while(right-left>1){

    int mid = left + (right-left)/2;

    if(dp[mid]>=target){
        right = mid;
    }else{
        left = mid;
    }


}
return right;

    }
}

