         int dp[prices.size() + 2][2];


        for(int day = (int)prices.size() + 1;day >= 0;day--){
            for(int buy = 0;buy <= 1;buy++){


                int &ans = dp[day][buy];

                if(day >= prices.size()){
                    ans = 0;
                }else{

                    // First Choice
                    int ans1 = dp[day + 1][buy]; // no transaction this say


