        for(int i = 0;i <= 2;i++){
                for(int j = 0;j <= 1;j++){
                    dp[i][j] = 0;
                }
        }

        for(int day = (int)prices.size() - 1;day >= 0;day--){
            for(int buy = 0;buy <= 1;buy++){


