class Solution {
public:
    int findMaxProfit(vector<int>&prices,bool f,int day){
        if(day>=prices.size())return 0;
        if(!f){
            int x = findMaxProfit(prices,!f,day+1) - prices[day];
            int y = findMaxProfit(prices,f,day+1);
            return max(x,y);
        }
			int x  = prices[day] + findMaxProfit(prices,!f,day+2);
			int y  = findMaxProfit(prices,f,day+1);
        return max(x,y); 
    }
    int maxProfit(vector<int>& prices) {
        return findMaxProfit(prices,false,0);
    }
};
```
### 2. Recoursion With Memo Solution | Top Down DP 
```
Time  Complexcity - O(N)
Space Complexcity - O(N)
```

```
class Solution {
public:
    int findMaxProfit(vector<int>&prices,bool f,int day,vector<vector<int>>&dp){
        if(day>=prices.size())return 0;
        if(dp[f][day]!=-1)return dp[f][day];
        if(!f){
            int x = findMaxProfit(prices,!f,day+1,dp) - prices[day];
            int y = findMaxProfit(prices,f,day+1,dp);
            return dp[f][day] = max(x,y);
        }
			int x  = prices[day] + findMaxProfit(prices,!f,day+2,dp);
			int y  = findMaxProfit(prices,f,day+1,dp);
        return dp[f][day] = max(x,y); 
    }
    int maxProfit(vector<int>& prices) {
        vector<vector<int>>dp(2,vector<int>(prices.size(),-1));
        return findMaxProfit(prices,false,0,dp);
    }
};
```

### 3. Bottom Up DP 
```
Time  Complexcity - O(N)
Space Complexcity - O(N)
```

```
class Solution {
public:
	int maxProfit(vector<int>& prices){
     int n = prices.size();
		if( n<= 1) return 0;
		vector<int> buy(n, 0),cd(n, 0),sell(n, 0);
		cd[0] = -prices[0],buy[0] = 0,sell[0] = INT_MIN;

		for (int i = 1; i < n; i++) {
			buy[i]  = max(buy[i - 1], sell[i - 1]);
			cd[i]   = max(cd[i - 1], buy[i - 1] - prices[i]);
			sell[i] = cd[i - 1] + prices[i];
		}
		return max(buy[n-1], sell[n-1]);
	}
};

```
### 4. Bottom Up DP || Less Memory
```
Time  Complexcity - O(N)
Space Complexcity - O(1)
```

```
class Solution {
public:
    int maxProfit(vector<int>& prices) {     
    int b = INT_MIN,s = 0,p_sell = 0,p_buy;
        for(int stock : prices){
            p_buy = b;
            b = max(b, p_sell - stock);
            p_sell = s;
            s = max(s, p_buy + stock);  
        }
        return s;
    }
};
```
