             int notsell = solve(prices,idx+1,true);
            return dp[idx][hold] = max(sell,notsell);
        }
    }

    int maxProfit(vector<int>& prices) {
        memset(dp,-1,sizeof(dp));
        return solve(prices,0,false);
    }
};
