public class BestTimeToBuyAndSellStockWithCooldownBottomUp1DApproach {
    public int maxProfit(int[] prices) {
        int[] profit = new int[3];

        for (int i = prices.length - 1; i >= 0; i--) {
            int prev = 0;

            for (int state = 2; state >= 0; state--) {
                if (state == 2) {
                    prev = profit[state];
                    profit[state] = profit[0];
                    continue;
                }

                int cur = profit[state];

                profit[state] = (state == 0) ?
                        Math.max(prev - prices[i], profit[state]) :
                        Math.max(prev + prices[i], profit[state]);

                prev = cur;
            }
        }

        return profit[0];
    }
}
