public class BestTimeToBuyAndSellStockWithCooldownMemoizationApproach {
    public int maxProfit(int[] prices) {
        int[][] memo = new int[prices.length][3];

        for (int r = 0; r < memo.length; r++) {
            Arrays.fill(memo[r], -1);
        }

        return maxProfit(0, 0, prices, memo);
    }

    private int maxProfit(int i, int state, int[] prices, int[][] memo) {
        if (i >= prices.length) return 0;
        if (state == 2) return maxProfit(i + 1, 0, prices, memo);
        if (memo[i][state] != -1) return memo[i][state];

        return memo[i][state] = state == 0?
            Math.max(maxProfit(i + 1, 1, prices, memo) - prices[i],
                     maxProfit(i + 1, state, prices, memo)) :
            Math.max(maxProfit(i + 1, 2, prices, memo) + prices[i],
                     maxProfit(i + 1, state, prices, memo));
