class Solution {
public:
    using VI = vector<int>;
    using fun = function<int(int, int)>;
    using Map = unordered_map<string, int>;
    int maxProfit(VI& A, Map m = {}, int NONE = -1) {
        int N = A.size();
        auto key = [](auto i, auto k) {
            stringstream ss; ss << i << "," << k;
            return ss.str();
        };
        fun go = [&](auto i, auto have) {
            if (N <= i)
                return 0;
            auto k = key(i, have);
            if (m.find(k) == m.end()) {
                auto buy = have == NONE ? go(i + 1, A[i]) : 0,
                     sell = have != NONE ? go(i + 2, NONE) + A[i] - have : 0,
                     keep = go(i + 1, have);
                m[k] = max({ buy, sell, keep });
            }
            return m[k];
        };
        return go(0, NONE);
    }
};
