            cur.none = max(cur.none, pre.sold);      // implicit hold when none[i] = none[i - 1]
            cur.have = max(cur.have, pre.none - x);  // implicit hold when have[i] = have[i - 1], -x because we buy at price x
            cur.sold = x + pre.have;                 // +x because we sell at price x
        }
        return max({ cur.none, cur.have, cur.sold });
