class Solution {
public:
    int maxProfit(vector<int>& prices) {
        int n = prices.size(),i;
        if(n < 2) return 0;
        //dp[i][0] --> we have zero stock in hand (cases: (we dont buy today and we have sell all) or (we sell today what we have bought))
        //dp[i][1] --> we have one stock in hand (cases: (we keep what we had on i-1) or (we buy today with amount we had on i-2 th day because of cool down one day gap(i-1)))
        vector<vector<int>> dp(n,vector<int>(2,0));
        dp[0][0] = 0; dp[0][1] = -prices[0];
        dp[1][0] = max(0,dp[0][1]+prices[1]); dp[1][1] = max(dp[0][1],-prices[1]);
        for(i = 2; i < n; i++){
            dp[i][0] = max(dp[i-1][0], dp[i-1][1] + prices[i]);
            //max of not buying today or selling the previous one today
            dp[i][1] = max(dp[i-2][0]-prices[i], dp[i-1][1]);
            //buy today making yesterday a cooldown period or keep the one bought yesterday
        }

        return max(dp[n-1][0], dp[n-1][1]);
    }
};

