class Solution {
    int [][] memo;
    public int maxProfit(int[] prices) {
        memo = new int[prices.length][2];
        for (int []m : memo) {
            Arrays.fill(m, -1);
        }
        int r = 0;
        for (int i=0; i<prices.length; i++) {
            r = Math.max(r, dfs(prices, i, false));
        }
        return r;

    }


    int dfs(int[] prices, int pos, boolean buy) {
        if(pos >= prices.length) {
            return 0;
        }
        int b = buy ? 0 : 1;
        if(memo[pos][b] != -1) {
            return memo[pos][b];
        }
        if(buy) {
            int sell = dfs(prices, pos + 2, false) + prices[pos];
            int notSell = dfs(prices, pos + 1, true);

            memo[pos][b] = Math.max(sell, notSell);
        }
        else {
            int tobuy = dfs(prices, pos +1, true) - prices[pos];
            int notBuy = dfs(prices, pos + 1, false);
            memo[pos][b] = Math.max(tobuy, notBuy);
        }

        return memo[pos][b];

    }
}
```

