***1. Recursive Solution*** (TLE for 2 testcases)
```
class Solution {
public:
    int recur(int i, vector<int>& p, bool buy){
        if(i>=p.size()) return 0;
        int profit=0;
        if(buy) profit = max(-p[i]+recur(i+1, p, false), recur(i+1, p, true));
        else profit = max(p[i]+recur(i+2, p, true), recur(i+1, p, false));
        return profit;
    }

    int maxProfit(vector<int>& prices) {
        return recur(0, prices, true);
    }
};
