class Solution {
public:
    int f(int ind , int buy, vector<int> &values,int n, vector<vector<int>>&dp)
{
        // buy =1 ; if the value of buy is 1 we have to buy a stock
        // buy =0 ; if the value of buy is 0 we have to sell a stock
    if(ind>=n) return 0;

    if(dp[ind][buy]!=-1) return dp[ind][buy];
    int profit =0;


    if(buy)
    {
        // We can either pick that stock to buy or notPick
        // In case of notPick add 0 to profit and move to next index
        // In case of pick,  Profit value is reduced by values[ind] and we move to the next index.
        profit = max(0+f(ind+1,1,values,n,dp), -values[ind] + f(ind+1,0,values,n,dp));
    }else{

         // We can either pick that stock to sell or notPick
        // In case of notPick add 0 to profit and move to next index
        // In case of pick profit value is increased by values[ind] and skip the next index as we cannot have any transaction on the next day and we shall move to index+2;
        profit = max(values[ind] + f(ind+2,1,values,n,dp), 0 + f(ind+1,0,values,n,dp));
    }

    return dp[ind][buy] =  profit;
}

    int maxProfit(vector<int>& prices) {
         int n = prices.size();
    vector<vector<int>>dp(n+1,vector<int>(2,-1));
    return f(0,1,prices,n,dp);
    }
};

