class Solution {
    public int maxProfit(int[] prices) {
        int[][] tab= new int[prices.length+1][2];
        tab[0][0]= 0; tab[1][0]= 0;
        tab[0][1]= Integer.MIN_VALUE; tab[1][1]= -prices[0];

        for (int i=2; i<=prices.length; i++){
            tab[i][0]= Math.max(tab[i-1][0],tab[i-1][1]+prices[i-1]);
            tab[i][1]= Math.max(tab[i-1][1],tab[i-2][0]-prices[i-1]);
        }

        return Math.max(tab[prices.length][0],tab[prices.length][1]);
    }
}
