```
class Solution {
public:
    int maxProfit(vector<int>& prices) {
        int n = prices.size();
        int dp[n][2]; //dp[0][0] represents profit at 1st day when we have no stock in hand
        //dp[0][1] represents profit at 1st day when we have stock in hand

        memset(dp, 0, sizeof(dp));

        if(n <= 1)
            return 0; //no profit if 1 day of profit;

        if(n == 2 && prices[1] > prices[0])
            return prices[1]-prices[0];
        else if(n == 2 && prices[1] < prices[0])
            return 0;

        //base condition
        dp[0][0] = 0;
        dp[0][1] = -prices[0];
        dp[1][0] = max(dp[0][1]+prices[1], dp[0][0]);
        dp[1][1] = max(-prices[1], dp[0][1]);

        //you have no stock when you sell the stock on ith day or carry forward your previous day's state
        //you have stock when you buy today or carry forward your previous day's state
		//dp[i-1][1]+prices[i] is when you sell today. 
		//If you sell today, you add the prices of ith day to profit until previous day
		//dp[i-2][0]-prices[i] is when you buy today.
		//Since we have cooldown of 1 day, we substract the prices[i] from profit of i-2th day

        for(int i = 2; i < n; i++){
            dp[i][0] = max(dp[i-1][0], dp[i-1][1]+prices[i]);
            dp[i][1] = max(dp[i-1][1], dp[i-2][0]-prices[i]);
        }

        return max(dp[n-1][0], dp[n-1][1]);
    }
};
