Top-down solution:-

class Solution {
public:

    int helper(vector<int>& prices, int n, int idx, int own, vector<vector<int>>& dp){

            if(idx >= n){
                return 0;
            }


            if(dp[idx][own] != -1)
                return dp[idx][own];

        if(own){

            // whether to sell or not to sell
            int op1 = prices[idx] + helper(prices, n, idx + 2, 0, dp);
            int op2 = helper(prices, n, idx + 1, own, dp);

            return dp[idx][own] = max(op1, op2);

        }
        else{

            // whether to buy or not
            int op1 = - prices[idx] + helper(prices, n, idx + 1, 1, dp);
            int op2 = helper(prices, n, idx + 1, own, dp);

            return dp[idx][own] = max(op1, op2);

        }

    }
    int maxProfit(vector<int>& prices) {

            int n = prices.size();

            vector<vector<int >> dp(n + 1, vector<int>(2, -1));
            int ans = helper(prices, n, 0, 0, dp);
            return ans;

    }
};

