# CODE
**Recursion + Memoization Method :**
```
class Solution {
public:
    int fun(vector<int> &pr , int ind , int st , vector<vector<int>> &dp)
    {
        if(ind >= pr.size())  return 0 ;
        if(dp[ind][st] != -1)  return dp[ind][st] ;

        int buy=0 ; 
        if( !st ) buy = max(-pr[ind] + fun(pr , ind+1 , 1 , dp) , fun(pr , ind+1 , 0 , dp) ) ;
        int sel=0 ;
        if( st )    sel = max( pr[ind] + fun(pr , ind+2 , 0 , dp) , fun(pr , ind+1 , 1 , dp) ) ;

        return dp[ind][st] = max(buy , sel) ;
    }
    int maxProfit(vector<int>& pr) {
        int n=pr.size() ;
        vector<vector<int>> dp(n , vector<int>(2 , -1)) ;
        return fun(pr , 0 , 0 , dp) ;
    }
};
```

**Tabulation Method :**
```
class Solution {
public:
    int maxProfit(vector<int>& pr) {
        int n=pr.size() ;
        vector<vector<int>> dp(n+2 , vector<int>(2 , 0)) ;

        for(int ind=n-1 ; ind>=0 ; ind--)
        {
            for(int st=0 ; st<2 ; st++)
            {
                int buy=0 ; 
                if( !st) buy = max(-pr[ind] + dp[ind+1][1] , dp[ind+1][0] ) ;
                int sel=0 ;
                if( st ) sel = max( pr[ind] + dp[ind+2][0] , dp[ind+1][1] ) ;

                dp[ind][st] = max(buy , sel) ;
            }
        }
        return dp[0][0] ;
    }
};
