def maxProfit(prices, index, buy, sell, cooldown):
    # base case: if the index is out of bounds, return 0
    if index >= len(prices):
        return 0

    # if the buy and sell variables are not set, then initialize them to 0
    if buy is None:
        buy = 0
    if sell is None:
        sell = 0

    # if we are in the cooldown period, we can only skip the day or sell the stock
    if cooldown > 0:
        return max(maxProfit(prices, index+1, buy, sell, cooldown-1), maxProfit(prices, index+1, buy, sell+prices[index], 0))

    # if we are not in the cooldown period, we have three options: skip the day, buy the stock, or sell the stock
    return max(maxProfit(prices, index+1, buy, sell, 0), maxProfit(prices, index+1, buy-prices[index], sell, 0), maxProfit(prices, index+1, buy, sell+prices[index], 0))
