class Solution:
    def findMinHeightTrees(self, n, E):
        if not E: return [0]
        G, seen = defaultdict(set), [False]*n
        for u,v in E:
            G[u].add(v)
            G[v].add(u)
        leaves, new_leaves, in_degree = [], [], []
        for i in range(n):
            if len(G[i]) == 1:
                leaves.append(i)
            in_degree.append(len(G[i]))
        while n > 2:
            for leaf in leaves:
                for adj in G[leaf]:
                    in_degree[adj] -= 1
                    if in_degree[adj] == 1:
                        new_leaves.append(adj)
            n -= len(leaves)
            leaves = new_leaves[:]
            new_leaves.clear()
        return leaves
