The idea is based on this question: https://leetcode.com/problems/sum-of-distances-in-tree/

```
class Solution {
public:
    int minh = INT_MAX; // minimum height
    vector<pair<int,int>> heights;
    // First we'll store heights with tree rooted at node 0
    int findHeight(vector<vector<int>>&graph,int u,int p)
    {
        int m1=0,m2=0,tmp;
        // m1 is max height, m2 is second max heigh
        for(int &v:graph[u])
        {
            if(v!=p)
            {
                tmp = 1 + findHeight(graph,v,u);
                if(tmp>m1)
                {
                    m2 = m1;
                    m1 =tmp;
                }
                else if(tmp>m2)
                {
                    m2 = tmp;
                }
            }
        }
        heights[u] = {m1,m2};
        return max(m1,m2);
    }
    void reroot(vector<vector<int>>&graph,int u,int p)
    {
       minh = min(minh,heights[u].first);
       int extra; //  extra height which the parent give to its child node

       for(int &v:graph[u])
       {
           if(v!=p)
           {
               // If child is in the path of maximum height of the parent, second maxmimum height of the    parent should be added

               if(heights[v].first+1==heights[u].first)
                   extra = heights[u].second;
               else
                   extra = heights[u].first;
               // Update the height of the child node accordingly
               if(1+extra>heights[v].first)
               {
                   heights[v].second = heights[v].first;
                   heights[v].first = 1 + extra;
               }
               else if(1+extra>heights[v].second)
               {
                   heights[v].second = 1 + extra;
               }

               reroot(graph,v,u);
           }
       }
    }
    vector<int> findMinHeightTrees(int n, vector<vector<int>>& edges) {
        vector<vector<int>> graph(n);
        heights.resize(n);
        for(vector<int>&edge:edges)
        {
            graph[edge[0]].push_back(edge[1]);
            graph[edge[1]].push_back(edge[0]);
        }

        findHeight(graph,0,-1);

        reroot(graph,0,-1);

        vector<int> ans;
        for(int i = 0;i<n;i++)
            if(heights[i].first==minh)
                ans.push_back(i);
        return ans;
    }
};
```

