class Solution {
    public List<Integer> findMinHeightTrees(int n, int[][] edges) {
        if(n<2){
            ArrayList<Integer> centroid = new ArrayList<>();
            for(int i=0; i<n; i++)
                centroid.add(i);
            return centroid;
        }
        ArrayList<Set<Integer>> adjacents  = new ArrayList<>();
        for(int i=0; i<n; i++)
            adjacents.add(new HashSet<Integer>());
        for(int[] edge : edges){
            Integer start = edge[0], end = edge[1];
            adjacents.get(start).add(end);
            adjacents.get(end).add(start);
        }
        ArrayList<Integer> leaves = new ArrayList<>();
        for(int i=0; i<n; i++){
            if(adjacents.get(i).size() == 1)
                leaves.add(i);
        }
        int remainingNodes = n;
        while(remainingNodes >2){
            remainingNodes -= leaves.size();
            ArrayList<Integer> newLeaves = new ArrayList<>();
            for(Integer leaf : leaves){
                for(Integer adjacent : adjacents.get(leaf)){
                    adjacents.get(adjacent).remove(leaf);
                    if(adjacents.get(adjacent).size() == 1)
                        newLeaves.add(adjacent);
                }
            }
            leaves = newLeaves;
        }
        return leaves;
    }
}
