    vector<int> findMinHeightTrees(int n, vector<vector<int>>& edges) {
        vector<int> adj[n];
        vector<int> degree(n, 0);
        //Make an adjacency list and count for degree of each node
        for(int i=0; i<edges.size(); i++) {
            adj[edges[i][0]].push_back(edges[i][1]);
            adj[edges[i][1]].push_back(edges[i][0]);

            degree[edges[i][0]]++;
            degree[edges[i][1]]++;
        }


        //Queue for BFS traversal
        queue<int> q;
        //Push all the nodes whose degrees are one because these nodes can't form minimum height trees
        for(int i=0; i<n; i++) {
            if(degree[i] == 1) {
                q.push(i);
            }
        }

        vector<int> res; //Resultant vector
        //Do BFS traversal to find out the nodes which are most connecting
        while(!q.empty()) {
            int size = q.size();
            vector<int> v; //Temporary vector
            while(size--) {
                int node = q.front();
                q.pop();
                v.push_back(node); //Push the node to temporary vector

                //Visit all the adjacent nodes
                for(auto it: adj[node]) {
                    degree[it]--; //Reduce degree since the node will be disconnected now

                    if(degree[it] == 1) {
                        q.push(it); //Push when degree becomes one
                    }
                }
            }
            res = v; //Result vector updated 
        }

        if(n == 1) res.push_back(0);
        return res;
    }

