class Solution {

    public List<Integer> findMinHeightTrees(int n, int[][] edges) {
        if (n == 1) {
            return Collections.singletonList(0);
        }

        int[] numEdges = new int[n];
        for (int[] edge : edges) {
            numEdges[edge[0]]++;
            numEdges[edge[1]]++;
        }

        int[][] adjacencyList = new int[n][];
        for (int i = 0; i < n; i++) {
            adjacencyList[i] = new int[numEdges[i]];
        }

        int[] work = new int[n];
        for (int[] edge : edges) {
            adjacencyList[edge[0]][work[edge[0]]++] = edge[1];
            adjacencyList[edge[1]][work[edge[1]]++] = edge[0];
        }

        int leafCount = 0;
        for (int i = 0; i < n; i++) {
            if (work[i] == 1) {
                work[leafCount++] = i;
            }
        }

        int[] work2 = new int[n];
        int nextLeafCount;

        int numVertices = n;
        while (numVertices > 2) {
            nextLeafCount = 0;
            for (int i = 0; i < leafCount; i++) {
                int leaf = work[i];
                for (int index : adjacencyList[leaf]) {
                    if (--numEdges[index] == 1) {
                        work2[nextLeafCount++] = index;
                    }
                }
            }

            numVertices -= leafCount;

            int[] w = work;
            work = work2;
            work2 = w;

            leafCount = nextLeafCount;
        }

        return leafCount == 2 ? Arrays.asList(work[0], work[1]) : Collections.singletonList(work[0]);
    }
}
