class Solution {
    public List<Integer> findMinHeightTrees(int n, int[][] edges) {
        List<Integer> res = new ArrayList<>();
        if (n == 1){
            res.add(0);
            return res;
        }
        //edge list
        HashMap<Integer, List<Integer>> map = new HashMap<>();
        for (int i = 0; i < n; i++) {
            map.put(i, new ArrayList<>());
        }
        //degree list
        int[] degree = new int[n];

        //construct edge list and degree list
        for (int[] edge: edges) {
            map.get(edge[0]).add(edge[1]);
            map.get(edge[1]).add(edge[0]);
            ++degree[edge[0]];
            ++degree[edge[1]];
        }

        Queue<Integer> q = new LinkedList<>();
        for (int i = 0; i < degree.length; i++) {
            if (degree[i] == 1) {
                q.add(i);
            }
        }

        //every time, remove current leaf nodes 
        //the left nodes have 2 cases: 
        //1) only root; 
        //2) 2 nodes connected by an edge, both can be root; 
        while (!q.isEmpty()) {
            List<Integer> list = new ArrayList<>();
            int size = q.size();
            for (int i = 0; i < size; i++) {
                int node = q.poll();
                list.add(node);
                degree[node] = 1;
                //like topological sort, remove nodes with 1 edge one by one.
                List<Integer> neighbours = map.get(node);
                for (int m = 0; m < neighbours.size(); m++) {
                    int v = neighbours.get(m);
                    --degree[v];
                    if (degree[v] == 1) {
                        q.add(v);
                    }
                }
            }
            res = list;
        }
        return res;
    }
}
