public class Solution {
  public List<int> FindMinHeightTrees(int n, int[,] edges) {
    List<int> res = new List<int>();
    if( n == 0 || edges == null) return res;
    int len = edges.GetLength(0);

    bool[] isRemoved =  new bool[n];
    int[][] matrix = new int[n][];
    for(int i = 0; i<n;i++)
    {
      matrix[i] = new int[n];
    }

    for(int i = 0; i < len ; i++)
    {
      int row = edges[i,0];
      int col =edges[i,1];
      matrix[row][col] = 1;
      matrix[col][row] = 1;
    }

    while(nodeleftcount(isRemoved) > 2)
    {
      List<int> leaves = FindLeaves(matrix, isRemoved);
      foreach(int leaf in leaves)
      {

        isRemoved[leaf] = true;
      }
    }

    for(int i = 0 ;i < n;i++)
    {
      if(!isRemoved[i]) res.Add(i);
    }

    return res;       
  }

  public List<int> FindLeaves(int[][] matrix, bool[] isRemoved)
  {
    List<int> leaves = new List<int>();
    for(int i = 0; i < matrix.Length; i++)
    {
      if(isRemoved[i]) continue;
      if(matrix[i].Sum() == 1) leaves.Add(i);
    }
    return leaves;
  }

  public int nodeleftcount(bool[] isRemoved)
  {
    int count = 0;
    foreach(bool i in isRemoved)
    {
      if(!i) count++;
    }
    return count;
  }
}
