C++ version (1100+ms):

    class Solution {
    public:
        vector<int> findMinHeightTrees(int n, vector<pair<int, int>> &edges) {

            if (n == 1)
                return { 0 };

            unordered_set<int> adj[n];
            for (auto &p : edges) {
                adj[p.first].insert(p.second);
                adj[p.second].insert(p.first);
            }

            vector<int> cur, next;
            for (int i = 0; i < n; i++)
                if (adj[i].size() == 1)
                    cur.push_back(i);

            while (n > 2) {
                for (int &v : cur) {
                    int w = *adj[v].begin();
                    adj[w].erase(v);
                    if (adj[w].size() == 1)
                        next.push_back(w);
                }
                n -= cur.size();
                cur.swap(next);
                next.clear();
            }
            return cur;
        }
    };

