            res.clear();
            int size=queue.size();
            for(int i=0;i<size;i++)
            {
                int cur=queue.front();
                queue.pop();
                res.push_back(cur);
                for(auto &neighbor:adj[cur])
                {
                    degrees[neighbor]--;
                    if(degrees[neighbor]==1)
                        queue.push(neighbor);
                }
            }
        }
        return res;
    }
};

