class Solution {
public:
    vector<int> findMinHeightTrees(int n, vector<vector<int>>& edges) {
        if(!n)
            return {};
        if(n == 1)
            return {0};

        vector<vector<int>> map(n);
        vector<int> degrees(n), heights(n);
        for(auto &e : edges)
        {
            map[e[0]].push_back(e[1]);
            map[e[1]].push_back(e[0]);
            degrees[e[0]]++;
            degrees[e[1]]++;
        }

        queue<int> q;
        for(int i = 0; i < n; ++i)
        {
            if(degrees[i] == 1)
                q.push(i);
        }

        int max_height = 0;
        while(q.size())
        {
            int node = q.front();
            q.pop();
            max_height = max(max_height, heights[node]);
            for(auto adjnode : map[node])
            {
                degrees[adjnode]--;
                if(degrees[adjnode] == 1)
                {
                    heights[adjnode] = max(heights[adjnode], heights[node]+1);
                    q.push(adjnode);
                }
            }
        }

        vector<int> res;
        for(int i = 0; i < n; ++i)
            if(heights[i] == max_height)
                res.push_back(i);
        return res;
    }
};

