class Solution {
    public List<Integer> findMinHeightTrees(int n, int[][] edges) {
        if (edges.length == 0 && n == 1) return new ArrayList<>(List.of(0));

        Map<Integer, List<Integer>> map = new HashMap<>(); 
        Queue<Integer> queue = new LinkedList<>();

        for (int[] edge : edges) {
            int x = edge[0];
            int y = edge[1];

            if (!map.containsKey(x)) {
                map.put(x, new ArrayList<>());
            }
            map.get(x).add(y);

            if (!map.containsKey(y)) {
                map.put(y, new ArrayList<>());
            }
            map.get(y).add(x);
        }

        if (map.size() < n-1) return new ArrayList<>();


        for (int i : map.keySet()) {
            if (map.get(i).size() == 1) {
                queue.add(i);
            }
        }

        while (!queue.isEmpty()) {
            if (map.size() < 3) return new ArrayList<>(map.keySet());
            int size = queue.size();
            while (size > 0) {
                int node = queue.poll();

                List<Integer> adjList = map.get(node);
                for (int adj : adjList) {
                    map.get(adj).remove(Integer.valueOf(node));
                    if (map.get(adj).size() == 1) {
                        queue.add(adj);
                    }

                }
                map.remove(node);
                size--;
            }
        }

        return new ArrayList<>(queue);     
    }
}
