            graph.get(edge[0]).add(edge[1]);
            if(!graph.containsKey(edge[1])){
                graph.put(edge[1], new ArrayList<Integer>());
            }
            graph.get(edge[1]).add(edge[0]);
            indegree[edge[0]]++;
            indegree[edge[1]]++;
        }

        for(int i = 0; i < n; i++) {
            if(indegree[i] == 1) {
                q.add(i);
            }
        }

        while(!q.isEmpty()){
            int len = q.size();
            res = new ArrayList<Integer>();

            for(int i = 0; i < len; i++){
                int curr = q.poll();
                res.add(curr);
                for(Integer x : graph.get(curr)){
                    indegree[x]--;
                    if(indegree[x] == 1){
                        q.add(x);
                    }
                }
            }
        }
        return res;
    }
}

