                leaves.append(i)
            }
        }
        while count > 2 {
            count -= leaves.count
            var newLeaves = [Int]()
            for leaf in leaves {
                let node = adj[leaf].first!
                adj[node].remove(leaf)
                if adj[node].count == 1 {
                    newLeaves.append(node)
                }
            }
            leaves = newLeaves
        }

        return leaves
    }
}
