class Solution {
private:
    void dfs(int vertex,int par,vector<int> g[],vector<int>&h,vector<int> &depth){
        // depth[vertex]=0;
        for(auto &child:g[vertex]){

            if(child==par) continue;
            depth[child]=depth[vertex]+1;
            dfs(child,vertex,g,h,depth);
            h[vertex]=max(h[vertex],h[child]+1);
        }
    }
public:
    vector<int> findMinHeightTrees(int n, vector<vector<int>>& edges) {
        vector<int> g[n];
        for(int i=0;i<edges.size();i++){
            g[edges[i][0]].push_back(edges[i][1]);
            g[edges[i][1]].push_back(edges[i][0]);
        }
        vector<int> height(n,0);
        vector<int> depth(n,0);

        if(n<=2){
            for(int i=0;i<n;i++) ans.push_back(i);
            return ans;
        }
        dfs(0,-1,g,height,depth);
        vector<int> height2(n,0);
        int maxd=*max_element(depth.begin(),depth.end());
        for(int i=0;i<n;i++){
            if(depth[i]==maxd){
                dfs(i,-1,g,height2,depth);
                break;
            }
        }
        int x=*max_element(height2.begin(),height2.end());
        vector<int> ans;
        for(int i=0;i<n;i++){

            if(x%2){
                if(height2[i]==x/2+1) ans.push_back(i);
                if(height2[i]==x/2) ans.push_back(i);
            }
            else {
               if(height2[i]==x/2) ans.push_back(i);
            }
        }
        return ans;
    }
};
