class Solution {
public:
    vector<int> findMinHeightTrees(int n, vector<vector<int>>& edges) {
        if(edges.size() == 0){
            vector<int> res;
            res.push_back(0);
            return res;
        }
        unordered_map<int,vector<int>> graph;
        vector<int> indegree(n);
        for(auto e:edges){
            indegree[e[0]]++;
            indegree[e[1]]++;
            graph[e[0]].push_back(e[1]);
            graph[e[1]].push_back(e[0]);
        }
        queue<int> q;
        for(int i = 0;i < n;i++){
            if(indegree[i] == 1)
                q.push(i);
        }
        while(n > 2){
            int size = q.size();
            n -= size;
            while(size-- > 0){
                int v = q.front();
                q.pop();
                for(auto vertex:graph[v]){
                    indegree[vertex]--;
                    if(indegree[vertex] == 1)
                        q.push(vertex);
                }
            }
        }
        vector<int> res;
        while(!q.empty()){
            res.push_back(q.front());
            q.pop();
        }
        return res;
    }
};
