class Solution {
public:
    struct graph{unordered_set <int> nb;};
    vector<int> findMinHeightTrees(int n, vector<vector<int>>& edges) {
        vector<int> rez;
        if(n==1) {rez.push_back(0);return rez;}
        if(n == 2) {rez.push_back(0);rez.push_back(1);return rez;}
        unordered_map <int,graph*> mp;
        for(int i = 0;i < n;i++){
            graph* tmp = new graph();
            mp[i] = tmp;
        }
        for(int i = 0;i < edges.size();i++){
            mp[edges[i][0]]->nb.insert(edges[i][1]);
            mp[edges[i][1]]->nb.insert(edges[i][0]);
        }

        stack <int> leaf;
        unordered_map <int,graph*>::iterator it = mp.begin();
        for(it ; it!=mp.end(); it++)
            if(it->second->nb.size() == 1) leaf.push (it->first);

        while(mp.size() > 2){
            stack <int> cand;
            while(!leaf.empty()){
                int n_leaf = leaf.top();
                leaf.pop();
                unordered_set <int>::iterator it = mp[n_leaf]->nb.begin();
                int nb_leaf = *it;
                mp.erase(n_leaf);
                mp[nb_leaf]->nb.erase(n_leaf);
                if(mp[nb_leaf]->nb.size() == 1) cand.push(nb_leaf);
            }
            while(!cand.empty()) {
                leaf.push(cand.top());
                cand.pop();
            } 
        }
         while(!leaf.empty()) {
                rez.push_back(leaf.top());
                leaf.pop();
            }

        return rez;
    }
};
