class Solution {
    public List<Integer> findMinHeightTrees(int n, int[][] edges) {
        // Build the adjacency list
        List<List<Integer>> graph = new ArrayList<>();
        for(int i = 0; i < n; i++) {
            graph.add(new ArrayList<>());
        }
        for(int[] edge : edges) {
            graph.get(edge[0]).add(edge[1]);
            graph.get(edge[1]).add(edge[0]);
        }


        Map<Integer, Map<Integer, Integer>> height = new HashMap<>();  // (node, parent) -> height map
        int[] treeHeight = new int[n];   // height for each root
        int minHeight = Integer.MAX_VALUE;   // min height in the `treeHeight`
        // try each node as the root node and calculate it's tree height
        for (int i = 0; i < n; i++) {
            List<Integer> children = graph.get(i);
            int childHeight = -1;
            // get the height of each child, using the current root as parent
            for(int child : children) {
                childHeight = Math.max(childHeight, getHeight(child, i, height, graph));
            }
            treeHeight[i] = childHeight + 1;   // calculate root's height
            minHeight = Math.min(minHeight, treeHeight[i]); // calculate global min height
        }
        // add all root's height = minHeight into the answer list
        List<Integer> ans = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            if(treeHeight[i] == minHeight) {
                ans.add(i);
            }
        }
        return ans;
    }

    // calculate the node's tree height when the parent node is `parent`
    // the result should be memorized in a map
    private int getHeight(int node, int parent, Map<Integer, Map<Integer, Integer>> height, List<List<Integer>> graph) {
        // if already seen, return
        if(height.containsKey(node) && height.get(node).containsKey(parent)) {
            return height.get(node).get(parent);
        }


        List<Integer> neighbors = graph.get(node);
        int treeHeight = 0;
        // if it has only one children, then it is the parent node, just return 0
        // otherwise iterate through all children's tree height
        if(neighbors.size() != 1){
            int maxChildHeight = -1;
            for(int n : neighbors) {
                if(n != parent) {
                    maxChildHeight = Math.max(maxChildHeight, getHeight(n, node, height, graph));
                }
            }    
            treeHeight = maxChildHeight + 1;
        }
        // update the (node, parent) ->height in the memo
        Map<Integer, Integer> childHeight = height.getOrDefault(node, new HashMap<>());
        childHeight.put(parent, treeHeight);
        height.put(node, childHeight);
        return treeHeight;
    }
}
