vector<int> findMinHeightTrees(int n, vector<pair<int, int>>& edges) {
	if (n == 1) {
		return {0};
	}
	vector<int> result;
	vector<vector<int>> graph(n, vector<int>());
	vector<int> degree(n, 0);
	queue<int> leafs;

	for (auto item: edges) {
		graph[item.first].push_back(item.second);
		graph[item.second].push_back(item.first);
		++ degree[item.first];
		++ degree[item.second];
	}

	for (int node = 0; node < n; ++ node) {
		if (degree[node] == 1) {
			leafs.push(node);
		}
	}

	int left = n;
	while (left > 2) {
		int leaf_size = leafs.size();
		for (int i = 0; i < leaf_size; ++ i) {
			int node = leafs.front();
			leafs.pop();
			-- left;
			for (auto peer_node : graph[node]) {
				-- degree[peer_node];
				if (degree[peer_node] == 1) {
					leafs.push(peer_node);
				}
			}
		}
	}

	while (!leafs.empty()) {
		result.push_back(leafs.front());
		leafs.pop();
	}
	return result;
}
