class Solution:
    def findMinHeightTrees(self, n: int, edges: List[List[int]]) -> List[int]:

        # represent the nodes and edges in a graph using a hashmap
        graph = { i: [] for i in range(n) }

        for edge in edges:
            node1, node2 = edge
            graph[node1].append(node2)
            graph[node2].append(node1)

        visited = set()
        heights = defaultdict(int)
        # dfs recursive function, dive into the tree from each node, and determine the heights of the trees from each node chosen as the root
        def dfs(node, parent, count, visited):
            heights[parent] = max(heights[parent], count)
            # dfs through the connections
            for c in graph[node]:
                if c not in visited:
                    visited.add(node)
                    dfs(c, parent, count + 1, visited)
                    # node has to be removed 'backtracking' to progress to the other child node from parent
                    visited.remove(node)

        for node in graph:
            dfs(node, node, 0, visited)

        # global minimum height
        min_height = min(heights.values())
        output = []

        for k, v in heights.items():
            if v == min_height:
                output.append(k)

        return output
