class Solution {
public:
    vector<int> findMinHeightTrees(int n, vector<vector<int>>& edges) {
        //constructing graph
        vector<vector<int>> adj(n+1, vector<int>());
        for(int i=0;i<edges.size(); i++){
            adj[edges[i][0]].push_back(edges[i][1]);
            adj[edges[i][1]].push_back(edges[i][0]);
        }

        //starting DFS with random node(for finding diameter)
        vector<int> depth(n+1, 0), parent(n+1, 0);
        dfs(adj,depth, parent, 0);

        int max_depth = INT_MIN, max_depth_node;
        for(int i=0;i<n;i++){
            if(depth[i] > max_depth){
                max_depth = depth[i];
                max_depth_node = i;
            }
            depth[i] = 0;
            parent[i] = 0;
        }
        //starting DFS second time
        dfs(adj, depth, parent, max_depth_node);

        max_depth = INT_MIN;
        for(int i=0;i<n;i++){
            if(depth[i] > max_depth){
                max_depth = depth[i];
                max_depth_node = i;
            }
        }

        //backtracking for forming path
        vector<int> path(max_depth+1);
        int temp = max_depth;
        while(max_depth != -1){
            path[max_depth] = max_depth_node;
            max_depth_node = parent[max_depth_node];
            max_depth--;
        }

        vector<int> ans;
        ans.push_back(path[temp/2]);
        if(temp%2){   
            ans.push_back(path[(temp+1)/2]);
        }
        return ans;
    }

private:
    void dfs(vector<vector<int>> &adj, vector<int>& depth, vector<int> &parent, int node, int par = -1){
        for(auto child: adj[node]){
            if(child == par) continue;
            depth[child] = depth[node]+1;
            parent[child] = node;
            dfs(adj, depth, parent, child, node);
        }
    }
};
