public List<Integer> findMinHeightTrees(int n, int[][] edges) {
	if(edges == null) return null;
	if(n < 2) return Collections.singletonList(0);

	//build adjacency list from given edge list
	Map<Integer, Set<Integer>> adj = new HashMap<>();
	for(int[] edge: edges) {
		adj.computeIfAbsent(edge[0], val -> new HashSet<>()).add(edge[1]);
		adj.computeIfAbsent(edge[1], val -> new HashSet<>()).add(edge[0]);
	}

	//removal from a linked list takes constant time
	Queue<Integer> leaves = new LinkedList<>(); 

	for(int i = 0; i < n; i++) {
		if(adj.get(i).size() == 1)
			leaves.add(i);
	}

	//keep iterating and pruning leaf nodes till number of nodes is less than 2
	while(n > 2) {
		int lsize = leaves.size();

		for(int i = 0; i < lsize; i++) {
			//get each leaf node from the queue in constant time 
			Integer leaf = leaves.remove();
			//reduce count of nodes
			n--;

			Set<Integer> leafNeighbors = adj.get(leaf);             
			for(Integer leafNeighbor: leafNeighbors) {
				//break connection of leaf node from adj list
				adj.get(leafNeighbor).remove(leaf);
				//add to queue if the neighbor becomes a leaf after removal
				if(adj.get(leafNeighbor).size() == 1)
					leaves.add(leafNeighbor);
			}
		}
	}
	return (List)leaves;
}
