class Solution {
    public List<Integer> findMinHeightTrees(int n, int[][] edges) {

        List<Integer> res = new ArrayList();

        // security check 1
        if(n <= 0) return res;

        // security check 2
        // returning the root node itself
        if(n == 1) {
            res.add(0);
            return res;
        }

        // degree
        int[] degree = new int[n];
        // adjacency list
        List<List<Integer>> adj = new ArrayList();

        // for each and every node add an empty adjacency list
        for(int i=0; i<n; i++) {
            adj.add(new ArrayList<>());
        }

        // adjacency list for every node is updated
        for(int[] e: edges) {
            degree[e[0]]++;
            degree[e[1]]++;
            adj.get(e[0]).add(e[1]);
            adj.get(e[1]).add(e[0]);
        }

        // BFS starts
        Queue<Integer> q = new LinkedList();

        for(int i=0; i<n; i++) {
            if(degree[i] == 1) {
                q.add(i);
            }
        }

        // There can be atmost 2 MHTs
        while(n > 2) {
            int size = q.size();
            n -= size;

            // if we remove a leaf node, the neighbouring nodes' degrees must be decremented 
            while(size --> 0) {
                int v = q.poll();
                for(int i: adj.get(v)) {
                    degree[i]--;
                    // the queue maintains all the nodes of degree 1
                    if(degree[i] == 1) {
                        q.add(i);
                    }
                }
            }
        }

        res.addAll(q);
        return res;
    }
}

