class Solution {
public:
    vector<int>v[20000+5];
    int visited[20000+5];
    int ht[20000+5];
    map<int,vector<int>>m;

    void calc_ht(int root){

        if (v[root].size()==1 && visited[v[root][0]]){
            ht[root]=0;
            return;
        }

        int ans=0;
        for (auto child:v[root]){
            if (!visited[child]){
                visited[child]=1;
                calc_ht(child);
                ans=max(ans,ht[child]);
            }
        }

        ht[root]=ans+1;

    }

    void dfs(int root,int parent){
        vector<int>temp;
        for (auto child:v[root]){
            temp.push_back(ht[child]);
